import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useCart } from "../cartContext";
import search from "../img/icon/search.png";
import iconCart from "../img/icon/cart.png";
import heart from "../img/icon/heart.png";
import aimo_logo from "../img/aimo_logo.png";
import Khmer_flag from "../img/khmerFlag.png";
import English_flag from "../img/englishFlag.png";

const Section = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { cart } = useCart();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const isKhmer = location.pathname.startsWith("/kh");
    const currentLanguage = location.pathname.split("/")[1] || "en";
    const currentPath = location.pathname;
    const totalProductCount = cart.reduce((total, product) => total + product.quantity, 0);
    const subtotal = cart.reduce((total, product) => total + product.price * product.quantity, 0);

    const handleLanguageSwitch = (language) => {
        const pathWithoutLanguage = location.pathname.split("/").slice(2).join("/");
        navigate(`/${language}/${pathWithoutLanguage}`);
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    useEffect(() => {
        const mediaQuery = window.matchMedia("(min-width: 768px)");
        const handleResize = () => {
            if (mediaQuery.matches) {
                setIsMenuOpen(false);
            }
        };

        mediaQuery.addEventListener("change", handleResize);
        return () => {
            mediaQuery.removeEventListener("change", handleResize);
        };
    }, []);

    return (
        <div>
            <header className="header">
                {/* Top Bar */}
                <div className="header__top">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-6 col-md-7">
                                <p>
                                    {isKhmer
                                        ? "ដឹកជញ្ចូនដោយឥតគិតថ្លៃ, ធានាសងប្រាក់គ្រប់វិញក្នុងរយៈពេល ៣០ ថ្ងៃ"
                                        : "Free shipping, 30-day return or refund guarantee."}
                                </p>
                            </div>
                            <div className="col-lg-6 col-md-5">
                                <div className="header__top__right">
                                    <a href="#">{isKhmer ? "ចុះឈ្មោះ" : "Sign in"}</a>
                                    <a href="#">{isKhmer ? "សំណួរខ្លីៗ" : "FAQs"}</a>
                                    <div>
                                        <span>
                                            {currentLanguage === "en" ? "English" : "Khmer"}
                                        </span>
                                        <ul style={{ width: "120px" }}>
                                            <li onClick={() => handleLanguageSwitch("en")}>
                                                <img
                                                    src={English_flag}
                                                    style={{ width: "30px", height: "30px", marginRight: "10px" }}
                                                />
                                                English
                                            </li>
                                            <li onClick={() => handleLanguageSwitch("kh")}>
                                                <img
                                                    src={Khmer_flag}
                                                    style={{ width: "30px", height: "30px", marginRight: "10px" }}
                                                />
                                                Khmer
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Main Navigation */}
                <div className="container">
                    <div className="row">
                        <div className="col-lg-3 col-md-3">
                            <Link to={`/${currentLanguage}/home`}>
                                <img src={aimo_logo} alt="Logo" style={{ width: "160px", height: "40px" }} />
                            </Link>
                        </div>
                        <div className="col-lg-6 col-md-6">
                            <nav className="header__menu mobile-menu">
                                <ul>
                                    <li>
                                        <Link to={`/${currentLanguage}/home`} style={{ fontWeight: "bold" }}>
                                            {isKhmer ? "ទំព័រដើម" : "Home"}
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to={`/${currentLanguage}/shop`} style={{ fontWeight: "bold" }}>
                                            {isKhmer ? "ទំនិញ" : "Shop"}
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to={`/${currentLanguage}/blog`} style={{ fontWeight: "bold" }}>
                                            {isKhmer ? "កំណត់ហេតុ" : "Blog"}
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to={`/${currentLanguage}/contact`} style={{ fontWeight: "bold" }}>
                                            {isKhmer ? "ទំនាក់ទំនង" : "Contact"}
                                        </Link>
                                    </li>
                                </ul>
                            </nav>
                        </div>
                        <div className="col-lg-3 col-md-3">
                            <div className="header__nav__option">
                                <a href="#">
                                    <img src={search} alt="Search" />
                                </a>
                                <a href="#">
                                    <img src={heart} alt="Wishlist" />
                                </a>
                                <Link to={`/${currentLanguage}/shopping-cart`}>
                                    <img src={iconCart} alt="Cart" />
                                    <span>{totalProductCount}</span>
                                </Link>
                                <div className="price">${subtotal.toFixed(2)}</div>
                            </div>
                        </div>
                    </div>
                    <div className="canvas__open" onClick={toggleMenu}>
                        <i className="fa fa-bars"></i>
                    </div>
                </div>
            </header>

            {/* Overlay and Sidebar */}
            {isMenuOpen && (
                <>
                    {/* Overlay */}
                    <div
                        style={{
                            position: "fixed",
                            top: 0,
                            left: 0,
                            width: "100%",
                            height: "100%",
                            backgroundColor: "rgba(0, 0, 0, 0.0)",
                            zIndex: 999,
                        }}
                        onClick={toggleMenu}
                    ></div>

                    {/* Sidebar Menu */}
                    <div
                        className="side-menu"
                        style={{
                            position: "fixed",
                            top: 0,
                            right: 0,
                            width: "300px",
                            height: "100%",
                            backgroundColor: "#fff",
                            zIndex: 1000,
                            padding: "20px",
                            boxShadow: "-2px 0 5px rgba(0, 0, 0, 0.5)",
                        }}
                    >
                        <button
                            onClick={toggleMenu}
                            style={{
                                position: "absolute",
                                top: "10px",
                                right: "10px",
                                backgroundColor: "white",
                                border: "1px solid red",
                                borderRadius: "50%",
                                width: "30px",
                                height: "30px",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                cursor: "pointer",
                            }}
                        >
                            X
                        </button>
                        <nav>
                            <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
                                <li>
                                    <Link to={`/${currentLanguage}/home`} style={{ fontWeight: "bold" }}>
                                        {isKhmer ? "ទំព័រដើម" : "Home"}
                                    </Link>
                                </li>
                                <li>
                                    <Link to={`/${currentLanguage}/shop`} style={{ fontWeight: "bold" }}>
                                        {isKhmer ? "ទំនិញ" : "Shop"}
                                    </Link>
                                </li>
                                <li>
                                    <Link to={`/${currentLanguage}/blog`} style={{ fontWeight: "bold" }}>
                                        {isKhmer ? "កំណត់ហេតុ" : "Blog"}
                                    </Link>
                                </li>
                                <li>
                                    <Link to={`/${currentLanguage}/contact`} style={{ fontWeight: "bold" }}>
                                        {isKhmer ? "ទំនាក់ទំនង" : "Contact"}
                                    </Link>
                                </li>
                            </ul>
                        </nav>
                    </div>
                </>
            )}
        </div>
    );
};

export default Section;
