import React from 'react'
import {Link, useLocation, useNavigate} from 'react-router-dom'
import { useCart } from '../cartContext'
import search from '../img/icon/search.png'
import iconCart from '../img/icon/cart.png'
import heart from '../img/icon/heart.png'
import aimo_logo from '../img/aimo_logo.png'
import Khmer_flag from '../img/khmerFlag.png'
import English_flag from '../img/englishFlag.png'

const Section = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const {cart} = useCart();
    const isKhmer = location.pathname.startsWith('/kh');
    const currentLanguage = location.pathname.split('/')[1] || 'en';
    const currentPath = location.pathname;
    const totalProductCount = cart.reduce((total, product) => total + product.quantity, 0);
    const subtotal = cart.reduce((total, product) => total + product.price * product.quantity, 0);

    const handleLanguageSwitch = (language) => {
        const pathWithoutLanguage = location.pathname.split('/').slice(2).join('/');
        navigate(`/${language}/${pathWithoutLanguage}`);
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen); // Toggle menu state
    };

    return (
        <div>
            <header className="header">
                <div className="header__top">
                    {/* Header top content */}
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-3 col-md-3">
                            <div className="header__logo">
                                <Link to={`/${currentLanguage}/home`}>
                                    <img src={aimo_logo} alt="" style={{ width: '160px', height: '40px' }} />
                                </Link>
                            </div>
                        </div>
                        <div className="col-lg-6 col-md-6">
                            {/* Main navigation */}
                        </div>
                        <div className="col-lg-3 col-md-3">
                            <div className="header__nav__option">
                                {/* Other options */}
                                <div className="canvas__open" onClick={toggleMenu}>
                                    <i className="fa fa-bars"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>

            {/* Sidebar/Menu */}
            {isMenuOpen && (
                <div className="side-menu">
                    <button onClick={toggleMenu} className="close-btn">X</button>
                    <ul>
                        <li><Link to={`/${currentLanguage}/home`}>Home</Link></li>
                        <li><Link to={`/${currentLanguage}/shop`}>Shop</Link></li>
                        <li><Link to={`/${currentLanguage}/contact`}>Contact</Link></li>
                    </ul>
                </div>
            )}

            <style>
                {`
                .side-menu {
                    position: fixed;
                    top: 0;
                    right: 0;
                    width: 250px;
                    height: 100%;
                    background: #fff;
                    box-shadow: -2px 0 5px rgba(0, 0, 0, 0.5);
                    z-index: 1000;
                    padding: 20px;
                }
                .close-btn {
                    background: none;
                    border: none;
                    font-size: 24px;
                    cursor: pointer;
                    margin-bottom: 20px;
                }
                .canvas__open {
                    cursor: pointer;
                    font-size: 24px;
                    color: #333;
                }
                `}
