                                          <li><Link to={`/${currentLanguage}/shopping-cart`}>{isKhmer ? 'កន្រ្ដកទំនិញ' : 'Shopping Cart'}</Link></li>
                                          <li><Link to={`/${currentLanguage}/checkout`}>{isKhmer ? 'ទូទាត់ទំនិញ' : 'Check Out'}</Link></li>
